import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [history, setHistory] = useState([]);

  const askQuestion = async () => {
    try {
      const res = await axios.post("http://localhost:5050/ask", { question });
      setAnswer(res.data.answer);
      fetchHistory();
    } catch (err) {
      console.error("âŒ Failed to get answer", err);
      setAnswer("Error: Unable to get response.");
    }
  };

  const fetchHistory = async () => {
    try {
      const res = await axios.get("http://localhost:5050/history");
      setHistory(res.data);
    } catch (err) {
      console.error("âŒ Failed to fetch history", err);
    }
  };

  useEffect(() => {
    fetchHistory();
  }, []);

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1>ðŸ§  AI Q&A Platform</h1>
      <input
        type="text"
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
        placeholder="Ask me anything..."
        style={{ width: "300px", padding: "8px", marginRight: "10px" }}
      />
      <button onClick={askQuestion} style={{ padding: "8px" }}>
        Ask
      </button>

      <div style={{ marginTop: "20px" }}>
        <strong>Answer:</strong>
        <p>{answer}</p>
      </div>

      <div style={{ marginTop: "40px" }}>
        <h2>ðŸ•“ History</h2>
        {history.map((item, index) => (
          <div
            key={index}
            style={{
              marginBottom: "1rem",
              padding: "10px",
              border: "1px solid #ddd",
            }}
          >
            <p><strong>Q:</strong> {item.question}</p>
            <p><strong>A:</strong> {item.answer}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
