import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [history, setHistory] = useState([]);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("token"));

  const askQuestion = async () => {
    try {
      const res = await axios.post(
        "http://localhost:5050/ask",
        { question },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAnswer(res.data.answer);
      fetchHistory();
    } catch (err) {
      console.error("❌ Failed to get answer", err);
      setAnswer("Error: Unable to get response.");
    }
  };

  const fetchHistory = async () => {
    try {
      const res = await axios.get("http://localhost:5050/history", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setHistory(res.data);
    } catch (err) {
      console.error("❌ Failed to fetch history", err);
    }
  };

  useEffect(() => {
    if (token) fetchHistory();
  }, [token]);

  const handleRegister = async () => {
    try {
      const res = await axios.post("http://localhost:5050/register", { email, password });
      localStorage.setItem("token", res.data.token);
      setToken(res.data.token);
      setIsLoggedIn(true);
    } catch (err) {
      alert("注册失败：" + err.response?.data?.error || err.message);
    }
  };

  const handleLogin = async () => {
    try {
      const res = await axios.post("http://localhost:5050/login", { email, password });
      localStorage.setItem("token", res.data.token);
      setToken(res.data.token);
      setIsLoggedIn(true);
    } catch (err) {
      alert("登录失败：" + err.response?.data?.error || err.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken("");
    setIsLoggedIn(false);
    setAnswer("");
    setHistory([]);
  };
  const clearHistory = async () => {
  try {
    await axios.delete("http://localhost:5050/history");
    setHistory([]); // 清空前端的状态
  } catch (err) {
    console.error("❌ Failed to clear history", err);
  }
};


  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      {!isLoggedIn ? (
        <div>
          <h2>登录 / 注册</h2>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{ marginRight: "10px" }}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ marginRight: "10px" }}
          />
          <button onClick={handleLogin}>登录</button>
          <button onClick={handleRegister} style={{ marginLeft: "10px" }}>
            注册
          </button>
        </div>
      ) : (
        <div>
          <h1>🧠 AI Q&A Platform</h1>
          <button onClick={handleLogout} style={{ marginBottom: "1rem" }}>退出登录</button>

          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="Ask me anything..."
            style={{ width: "300px", padding: "8px", marginRight: "10px" }}
          />
          <button onClick={askQuestion} style={{ padding: "8px" }}>Ask</button>

          <div style={{ marginTop: "20px" }}>
            <strong>Answer:</strong>
            <p>{answer}</p>
          </div>

          <div style={{ marginTop: "40px" }}>
            <h2>🕓 History</h2>
            {history.map((item, index) => (
              <div
                key={index}
                style={{
                  marginBottom: "1rem",
                  padding: "10px",
                  border: "1px solid #ddd",
                }}
              >
                <p>
                  <strong>Q:</strong> {item.question}
                </p>
                <p>
                  <strong>A:</strong> {item.answer}
                </p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
